import React from 'react';

class ReviewAudiences extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      replies: [], // Array to store the replies
    };
  }

  handleReplySubmit = (index, reply) => {
    const { replies } = this.state;
    replies[index] = reply;
    this.setState({ replies });
  };

  render() {
    const { reviews } = this.props;
    const { replies } = this.state;

    return (
      <div className="container mt-5">
        <h2>Review Audiences</h2>
        <ul className="list-group">
          {reviews.map((review, index) => (
            <li key={index} className="list-group-item">
              <div className="d-flex align-items-center">
                <img
                  src={`https://dummyimage.com/50x50/c0c0c0/ffffff&text=Profile`}
                  alt="Profile"
                  className="rounded-circle mr-2"
                />
                <strong>Review:</strong> {review}
              </div>
              <div className="mt-3">
                <form
                  onSubmit={(e) => {
                    e.preventDefault();
                    const reply = e.target.elements.reply.value;
                    this.handleReplySubmit(index, reply);
                    e.target.reset();
                  }}
                >
                  <div className="form-group">
                    <label htmlFor={`reply-${index}`}>Reply:</label>
                    <input
                      type="text"
                      className="form-control"
                      id={`reply-${index}`}
                      name="reply"
                      placeholder="Enter your reply"
                      required
                    />
                  </div>
                  <button type="submit" className="btn btn-primary">Submit</button>
                </form>
                {replies[index] && (
                  <div className="mt-2">
                    <strong>Your Reply:</strong> {replies[index]}
                  </div>
                )}
              </div>
            </li>
          ))}
        </ul>
      </div>
    );
  }
}

export default ReviewAudiences;
