import React from 'react';
import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      reviews: [],
      newReview: '',
      comments: [],
      newComment: '',
      listings: [
        // your listings data here
      ],
      userEmail: '',
      userStatus: '',
      filterType: 'All',
    };
  }

  componentDidMount() {
    // Retrieve comments from local storage and update the state
    const storedComments = localStorage.getItem('comments');
    if (storedComments) {
      this.setState({ comments: JSON.parse(storedComments) });
    }
  }

  componentDidUpdate() {
    // Save comments to local storage whenever the component updates
    const { comments } = this.state;
    localStorage.setItem('comments', JSON.stringify(comments));
  }

  handleReviewInputChange = (event) => {
    this.setState({ newReview: event.target.value });
  };

  handleCommentInputChange = (event) => {
    this.setState({ newComment: event.target.value });
  };

  handleReviewFormSubmit = (event, index) => {
    event.preventDefault();
    const { newReview, reviews } = this.state;
    if (newReview.trim() !== '') {
      const updatedReviews = [...reviews];
      updatedReviews[index] = newReview;
      this.setState({
        reviews: updatedReviews,
        newReview: '',
      });
    }
  };

  handleCommentFormSubmit = (event) => {
    event.preventDefault();
    const { newComment, userEmail, userStatus, comments } = this.state;
    if (newComment.trim() !== '') {
      const newCommentObj = {
        email: userEmail,
        status: userStatus,
        comment: newComment,
      };
      this.setState((prevState) => ({
        comments: [...prevState.comments, newCommentObj],
        newComment: '',
      }));
    }
  };

  handleEmailInputChange = (event) => {
    this.setState({ userEmail: event.target.value });
  };

  handleStatusInputChange = (event) => {
    this.setState({ userStatus: event.target.value });
  };

  handleFilterTypeChange = (event) => {
    this.setState({ filterType: event.target.value });
  };

  render() {
    const { listings, reviews, newReview, comments, newComment, userEmail, userStatus, filterType } = this.state;

    const filteredListings = filterType === 'All' ? listings : listings.filter((listing) => listing.type === filterType);

    return (
      <div>
        <div className="container mt-5" id="review">
          <h2>
            Reviews
            <select className="form-control d-inline-block ml-3" value={filterType} onChange={this.handleFilterTypeChange}>
              <option value="All">All</option>
              <option value="Apartment">Apartment</option>
              <option value="Sharehouse">Sharehouse</option>
              <option value="Student Accommodation">Student Accommodation</option>
            </select>
          </h2>
          <div className="row">
            {filteredListings.map((listing, index) => (
              <div key={index} className="col-md-4 mb-4">
                <div className="card">
                  <div className="img-container">
                    <img src={listing.image} className="card-img-top" alt="House" />
                  </div>
                  <div className="card-body">
                    <h5 className="card-title">
                      <a>Address: </a>
                      {/* connect to gmaps */}
                      <a href={`https://www.google.com/maps/search/?api=1&query=${encodeURIComponent(listing.address)}`} target="_blank" rel="noopener noreferrer">{listing.address}
                      </a>
                    </h5>
                    <p className="card-text">Type: {listing.type}</p>
                    <form onSubmit={(event) => this.handleReviewFormSubmit(event, index)}>
                      <div className="form-group">
                        <label htmlFor="reviewInput">Add Review Summary:</label>
                        <textarea
                          className="form-control"
                          id="reviewInput"
                          rows="3"
                          value={newReview}
                          onChange={this.handleReviewInputChange}
                        ></textarea>
                      </div><br></br>
                      <button type="submit" className="btn btn-primary mb-2">
                        Submit Review
                      </button>
                    </form>
                    {reviews[index] && (
                      <div className="card mb-2">
                        <div className="card-body">
                          {comments.map((comment, i) => (
                            <div key={i} className="card bg-light mt-2">
                              <div className="card-body">
                                <h3 className="card-text">{reviews[index]}</h3>
                                <p className="card-text">
                                  <strong>Email:</strong> {comment.email}
                                </p>
                                <p className="card-text">
                                  <strong>Status:</strong> {comment.status}
                                </p>
                                <p className="card-text">
                                  <strong>Comment:</strong> {comment.comment}
                                </p>
                              </div>
                            </div>
                          ))}
                          <form onSubmit={this.handleCommentFormSubmit}>
                            <div className="form-row align-items-center">
                              <div className="col">
                                <input
                                  type="email"
                                  className="form-control mb-2"
                                  placeholder="Email"
                                  value={userEmail}
                                  onChange={this.handleEmailInputChange}
                                />
                              </div>
                              <div className="col">
                                <select
                                  className="form-control mb-2"
                                  value={userStatus}
                                  onChange={this.handleStatusInputChange}
                                >
                                  <option value="">Select Status</option>
                                  <option value="Former Tenant">Former Tenant</option>
                                  <option value="Current Tenant">Current Tenant</option>
                                </select>
                              </div>
                              <div className="col">
                                <textarea
                                  className="form-control mb-2"
                                  placeholder="Add your comment..."
                                  value={newComment}
                                  onChange={this.handleCommentInputChange}
                                ></textarea>
                              </div>
                              <div className="col">
                                <button type="submit" className="btn btn-primary mb-2">
                                  Submit Comment
                                </button>
                              </div>
                            </div>
                          </form>
                        </div>
                      </div>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  }
}

export default App;
