import React from 'react';
import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      reviews: [],
      newReview: '',
      comments: [],
      listings: [
        {
          image: 'https://i2.au.reastatic.net/800x600-format=webp/834c6299504b1fa49683d16ec8aad29a115b2c914e5e4f2922e6ec46d0981075/image.jpg',
          address: '1105/442 Elizabeth Street, Melbourne, VIC 3000',
          type: 'Apartment',
        },
        {
          image: 'https://i2.au.reastatic.net/800x600-format=webp/41f681b4e16b15fab09fe9feca04ea93b0f275a9168f1e1157980f4ffa83da95/image.jpg',
          address: '163 Albert Street, Port Melbourne, VIC 3207',
          type: 'Sharehouse',
        },
        {
          image: 'https://i2.au.reastatic.net/1096x744-resize,extend,r=33,g=40,b=46/166b0483a7db77277c3cadea57848c2c2f8818855c18492941361ee549437b6b/image.jpg',
          address: '108/593 Elizabeth Street, Melbourne, VIC 3000',
          type: 'Student Accommodation',
        },
      ],
      userEmail: '',
      userStatus: '',
      filterType: 'All',
    };
  }

  handleReviewInputChange = (event) => {
    this.setState({ newReview: event.target.value });
  };

  handleCommentFormSubmit = (event, index) => {
    event.preventDefault();
    const { newReview, reviews, userEmail, userStatus, comments } = this.state;
    if (newReview.trim() !== '') {
      const updatedReviews = [...reviews];
      updatedReviews[index] = newReview;
      const newCommentObj = {
        email: userEmail,
        status: userStatus,
        comment: newReview,
      };
      this.setState((prevState) => ({
        reviews: updatedReviews,
        comments: [...prevState.comments, newCommentObj],
        newReview: '',
        userEmail: '',
        userStatus: '',
      }));
    }
  };

  handleEmailInputChange = (event) => {
    this.setState({ userEmail: event.target.value });
  };

  handleStatusInputChange = (event) => {
    this.setState({ userStatus: event.target.value });
  };

  handleFilterTypeChange = (event) => {
    this.setState({ filterType: event.target.value });
  };

  render() {
    const { listings, reviews, newReview, comments, userEmail, userStatus, filterType } = this.state;

    const filteredListings = filterType === 'All' ? listings : listings.filter((listing) => listing.type === filterType);

    return (
      <div>
        <div className="container mt-5" id="review">
          <h2>
            Reviews
            <select className="form-control d-inline-block ml-3" value={filterType} onChange={this.handleFilterTypeChange}>
              <option value="All">All</option>
              <option value="Apartment">Apartment</option>
              <option value="Sharehouse">Sharehouse</option>
              <option value="Student Accommodation">Student Accommodation</option>
            </select>
          </h2>
          <div className="row">
            {filteredListings.map((listing, index) => (
              <div key={index} className="col-md-4 mb-4">
                <div className="card">
                  <div className="img-container">
                    <img src={listing.image} className="card-img-top" alt="House" />
                  </div>
                  <div className="card-body">
                    <h5 className="card-title">
                      <a>Address: </a>
                      {/* connect to gmaps */}
                      <a href={`https://www.google.com/maps/search/?api=1&query=${encodeURIComponent(listing.address)}`} target="_blank" rel="noopener noreferrer">{listing.address}
                      </a>
                    </h5>
                    <p className="card-text">Type: {listing.type}</p>
                    <form onSubmit={(event) => this.handleCommentFormSubmit(event, index)}>
                      <div className="form-group">
                        <label htmlFor="reviewInput">Add Review:</label>
                        <textarea
                          className="form-control"
                          id="reviewInput"
                          rows="3"
                          value={newReview}
                          onChange={this.handleReviewInputChange}
                        ></textarea>
                      </div><br></br>
                      <button type="submit" className="btn btn-primary mb-2">
                        Submit Review and Comment
                      </button>
                    </form>
                    {reviews[index] && (
                      <div className="card mb-2">
                        <div className="card-body">
                          {comments.map((comment, i) => (
                            <div key={i} className="card bg-light mt-2">
                              <div className="card-body">
                                <h3 className="card-text">{reviews[index]}</h3>
                                <p className="card-text">
                                  <strong>Email:</strong> {comment.email}
                                </p>
                                <p className="card-text">
                                  <strong>Status:</strong> {comment.status}
                                </p>
                                <p className="card-text">
                                  <strong>Comment:</strong> {comment.comment}
                                </p>
                              </div>
                            </div>
                          ))}
                        </div>
                      </div>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  }
}

export default App;
