import React from 'react';
import { generateAvatar } from 'avatar-initials';

class Reviewer extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      review: '',
      profilePicture: '',
    };
  }

  handleReviewChange = (event) => {
    this.setState({ review: event.target.value });
  };

  handleSubmit = (event) => {
    event.preventDefault();
    const profilePicture = generateAvatar();
    this.props.onReviewSubmit(this.state.review, profilePicture);
    this.setState({ review: '', profilePicture });
  };

  render() {
    return (
      <div className="container mt-5">
        <h2>Reviewer</h2>
        <form onSubmit={this.handleSubmit}>
          <div className="form-group">
            <textarea
              className="form-control"
              value={this.state.review}
              onChange={this.handleReviewChange}
              placeholder="Write your review here"
              required
            ></textarea>
          </div>
          <button type="submit" className="btn btn-primary">Submit Review</button>
        </form>
        {this.state.profilePicture && (
          <img src={this.state.profilePicture} alt="Profile" className="mt-3 rounded-circle" style={{ width: '100px', height: '100px' }} />
        )}
      </div>
    );
  }
}

export default Reviewer;
