import React from 'react';

class ReviewAudiences extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      comments: [],
      newComment: '',
    };
  }

  handleInputChange = (event) => {
    this.setState({ newComment: event.target.value });
  };

  handleFormSubmit = (event) => {
    event.preventDefault();
    const { newComment } = this.state;
    if (newComment.trim() !== '') {
      this.setState((prevState) => ({
        comments: [...prevState.comments, newComment],
        newComment: '',
      }));
    }
  };

  render() {
    const { comments, newComment } = this.state;

    return (
      <div className="container mt-5">
        <h2>Reviews</h2>
        <ul className="list-group">
          <li className="list-group-item">
            <div className="d-flex align-items-center">
              <img
                src="https://i2.au.reastatic.net/800x600-format=webp/834c6299504b1fa49683d16ec8aad29a115b2c914e5e4f2922e6ec46d0981075/image.jpg"
                alt="House"
                className="rounded mr-2"
                style={{ width: '100px', height: '100px' }}
              />
              <div>
                <strong>Address: </strong>1105/442 Elizabeth Street, Melbourne, VIC 3000
                <br />
                <strong>Type: </strong>Apartment
              </div>
            </div>
            <div className="mt-2">
              <form onSubmit={this.handleFormSubmit}>
                <div className="form-group">
                  <label htmlFor="emailInput">Email:</label>
                  <input type="email" className="form-control" id="emailInput" required />
                </div>
                <div className="form-group">
                  <label htmlFor="statusSelect">Status:</label>
                  <select className="form-control" id="statusSelect" required>
                    <option value="">-- Select --</option>
                    <option value="current">Current Tenant</option>
                    <option value="former">Former Tenant</option>
                  </select>
                </div>
                <div className="form-group">
                  <label htmlFor="reviewTextarea">Review:</label>
                  <textarea className="form-control" id="reviewTextarea" rows="3" required></textarea>
                </div>
                <button type="submit" className="btn btn-primary">Submit Review</button>
              </form>
            </div>
            <ul className="list-group mt-2">
              {comments.map((comment, idx) => (
                <li key={idx} className="list-group-item">
                  <div className="d-flex align-items-center">
                    <img
                      src="https://dummyimage.com/50x50/c0c0c0/ffffff&text=Profile"
                      alt="Profile"
                      className="rounded-circle mr-2"
                    />
                    {comment}
                  </div>
                </li>
              ))}
            </ul>
          </li>
        </ul>
      </div>
    );
  }
}

export default ReviewAudiences;
