import React from 'react';

class ReviewAudiences extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      comments: [],
      newComments: Array(props.reviews.length).fill(''),
    };
  }

  handleInputChange = (event, index) => {
    const { value } = event.target;
    this.setState((prevState) => {
      const newComments = [...prevState.newComments];
      newComments[index] = value;
      return { newComments };
    });
  };

  handleFormSubmit = (event, index) => {
    event.preventDefault();
    const { newComments } = this.state;
    const comment = newComments[index].trim();
    if (comment !== '') {
      this.setState((prevState) => {
        const comments = [...prevState.comments, comment];
        const newComments = [...prevState.newComments];
        newComments[index] = '';
        return { comments, newComments };
      });
    }
  };

  render() {
    const { reviews } = this.props;
    const { comments, newComments } = this.state;

    return (
      <div className="container mt-5">
        <h2>Review Audiences</h2>
        <ul className="list-group">
          {reviews.map((review, index) => (
            <li key={index} className="list-group-item">
              <div className="d-flex align-items-center">
                <img
                  src={review.image}
                  alt="House"
                  className="rounded mr-2"
                  style={{ width: '100px', height: '100px' }}
                />
                <div>
                  <h5>Address: {review.address}</h5>
                  <p>Type: {review.type}</p>
                </div>
              </div>
              <div className="mt-2">
                <form onSubmit={(event) => this.handleFormSubmit(event, index)}>
                  <div className="form-group">
                    <label htmlFor={`comment-${index}`}>Add your comment:</label>
                    <input
                      type="text"
                      className="form-control"
                      id={`comment-${index}`}
                      placeholder="Enter your comment"
                      value={newComments[index]}
                      onChange={(event) => this.handleInputChange(event, index)}
                    />
                  </div>
                  <button className="btn btn-primary" type="submit">
                    Submit
                  </button>
                </form>
              </div>
              <ul className="list-group mt-2">
                {comments.map((comment, idx) => (
                  <li key={idx} className="list-group-item">
                    <div className="d-flex align-items-center">
                      <img
                        src={`https://dummyimage.com/50x50/c0c0c0/ffffff&text=Profile`}
                        alt="Profile"
                        className="rounded-circle mr-2"
                      />
                      {comment}
                    </div>
                  </li>
                ))}
              </ul>
            </li>
          ))}
        </ul>
      </div>
    );
  }
}

export default ReviewAudiences;
