import React, { Component } from "react";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      listings: [
        {
          address: "123 Main St",
          type: "Apartment",
          image: "apartment.jpg",
        },
        {
          address: "456 Elm St",
          type: "House",
          image: "house.jpg",
        },
      ],
      newReview: "",
      reviews: [],
      userEmail: "",
      userStatus: "",
      newComment: "",
      comments: [],
    };
  }

  handleReviewFormSubmit = (event, index) => {
    event.preventDefault();
    const { newReview, reviews } = this.state;
    const updatedReviews = [...reviews];
    updatedReviews[index] = newReview;
    this.setState({ reviews: updatedReviews, newReview: "" });
  };

  handleReviewInputChange = (event) => {
    this.setState({ newReview: event.target.value });
  };

  handleCommentFormSubmit = (event) => {
    event.preventDefault();
    const { userEmail, userStatus, newComment, comments } = this.state;
    const newCommentObj = {
      email: userEmail,
      status: userStatus,
      comment: newComment,
    };
    const updatedComments = [...comments, newCommentObj];
    this.setState({ comments: updatedComments, userEmail: "", userStatus: "", newComment: "" });
  };

  handleEmailInputChange = (event) => {
    this.setState({ userEmail: event.target.value });
  };

  handleStatusInputChange = (event) => {
    this.setState({ userStatus: event.target.value });
  };

  handleCommentInputChange = (event) => {
    this.setState({ newComment: event.target.value });
  };

  render() {
    const { listings, newReview, reviews, userEmail, userStatus, newComment, comments } = this.state;

    return (
      <div>
        <div className="container mt-5">
          <h2>What is Ranter?</h2>
          <p>
            Are you tired of struggling to find the perfect rental property? Look no further than Ranter! Our platform
            provides tenants with the most current and authentic reviews from previous renters, helping you to find your
            ideal home. Say goodbye to the guesswork and frustration of the rental search process and say hello to the
            ease and convenience of Ranter!
          </p>
        </div>

        <div className="container mt-5" id="review">
          <h2>Reviews</h2>
          {listings.map((listing, index) => (
            <div key={index} className="card mb-3">
              <div className="row no-gutters">
                <div className="col-md-4">
                  <img src={listing.image} className="card-img" alt="House" />
                </div>
                <div className="col-md-8">
                  <div className="card-body">
                    <h5 className="card-title">Address: {listing.address}</h5>
                    <p className="card-text">Type: {listing.type}</p>
                    <form onSubmit={(event) => this.handleReviewFormSubmit(event, index)}>
                      <div className="form-group">
                        <label htmlFor="reviewInput">Add Review:</label>
                        <textarea
                          className="form-control"
                          id="reviewInput"
                          rows="3"
                          value={newReview}
                          onChange={this.handleReviewInputChange}
                        ></textarea>
                      </div>
                      <button type="submit" className="btn btn-primary">
                        Submit Review
                      </button>
                    </form>
                    {reviews[index] && (
                      <div className="mt-3">
                        <h6>Reviews:</h6>
                        <p>{reviews[index]}</p>
                      </div>
                    )}
                    <hr />
                    <h6>Comments:</h6>
                    {comments.map((comment, index) => (
                      <div key={index}>
                        <p>
                          <strong>Email:</strong> {comment.email}
                        </p>
                        <p>
                          <strong>Status:</strong> {comment.status}
                        </p>
                        <p>{comment.comment}</p>
                      </div>
                    ))}
                    <form onSubmit={this.handleCommentFormSubmit}>
                      <div className="form-group">
                        <label htmlFor="emailInput">Email:</label>
                        <input
                          type="email"
                          className="form-control"
                          id="emailInput"
                          value={userEmail}
                          onChange={this.handleEmailInputChange}
                        />
                      </div>
                      <div className="form-group">
                        <label htmlFor="statusInput">Status:</label>
                        <input
                          type="text"
                          className="form-control"
                          id="statusInput"
                          value={userStatus}
                          onChange={this.handleStatusInputChange}
                        />
                      </div>
                      <div className="form-group">
                        <label htmlFor="commentInput">Comment:</label>
                        <textarea
                          className="form-control"
                          id="commentInput"
                          rows="2"
                          value={newComment}
                          onChange={this.handleCommentInputChange}
                        ></textarea>
                      </div>
                      <button type="submit" className="btn btn-primary">
                        Submit Comment
                      </button>
                    </form>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  }
}

export default App;
