import React from 'react';
import './App.css';
import { Navbar, Nav } from 'react-bootstrap';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      reviews: [],
      newReview: '',
      comments: [],
      newComment: '',
      listings: [
        {
          image: 'https://i2.au.reastatic.net/800x600-format=webp/834c6299504b1fa49683d16ec8aad29a115b2c914e5e4f2922e6ec46d0981075/image.jpg',
          address: '1105/442 Elizabeth Street, Melbourne, VIC 3000',
          type: 'Apartment',
        },
        {
          image: 'https://i2.au.reastatic.net/800x600-format=webp/41f681b4e16b15fab09fe9feca04ea93b0f275a9168f1e1157980f4ffa83da95/image.jpg',
          address: '163 Albert Street, Port Melbourne, VIC 3207',
          type: 'Sharehouse',
        },
        {
          image: 'https://i2.au.reastatic.net/600x800-resize,extend,r=33,g=40,b=46/e8e44ba7cbc7eec3f3e7681842b9dd6d3dc5375dbe1d072e172687996831b898/image.jpg',
          address: '108/593 Elizabeth Street, Melbourne, VIC 3000',
          type: 'Student Accommodation',
        },
      ],
      userEmail: '',
      userStatus: '',
    };
  }

  handleReviewInputChange = (event) => {
    this.setState({ newReview: event.target.value });
  };

  handleCommentInputChange = (event) => {
    this.setState({ newComment: event.target.value });
  };

  handleReviewFormSubmit = (event) => {
    event.preventDefault();
    const { newReview } = this.state;
    if (newReview.trim() !== '') {
      this.setState((prevState) => ({
        reviews: [...prevState.reviews, newReview],
        newReview: '',
      }));
    }
  };

  handleCommentFormSubmit = (event) => {
    event.preventDefault();
    const { newComment } = this.state;
    if (newComment.trim() !== '') {
      this.setState((prevState) => ({
        comments: [...prevState.comments, newComment],
        newComment: '',
      }));
    }
  };

  handleEmailInputChange = (event) => {
    this.setState({ userEmail: event.target.value });
  };

  handleStatusInputChange = (event) => {
    this.setState({ userStatus: event.target.value });
  };

  render() {
    const { listings, reviews, newReview, comments, newComment, userEmail, userStatus } = this.state;

    return (
      <div>
        <Navbar bg="light" expand="lg">
          <Navbar.Brand href="/">Ranter</Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="ml-auto">
              <Nav.Link href="#review">Review</Nav.Link>
            </Nav>
          </Navbar.Collapse>
        </Navbar>

        <div className="container mt-5">
          <h2>What is Ranter?</h2>
          <p>
            Are you tired of struggling to find the perfect rental property? Look no further than Ranter! Our platform provides tenants with the most current and authentic reviews from previous renters, helping you to find your ideal home. Say goodbye to the guesswork and frustration of the rental search process and say hello to the ease and convenience of Ranter!
          </p>
        </div>

        <div className="container mt-5" id="review">
          <h2>Reviews</h2>
          {listings.map((listing, index) => (
            <div key={index} className="card mb-3">
              <div className="row no-gutters">
                <div className="col-md-4">
                  <img src={listing.image} className="card-img" alt="House" />
                </div>
                <div className="col-md-8">
                  <div className="card-body">
                    <h5 className="card-title">Address: {listing.address}</h5>
                    <p className="card-text">Type: {listing.type}</p>
                    <form onSubmit={this.handleReviewFormSubmit}>
                      <div className="form-group">
                        <label htmlFor="reviewInput">Add Review:</label>
                        <textarea
                          className="form-control"
                          id="reviewInput"
                          rows="3"
                          value={newReview}
                          onChange={this.handleReviewInputChange}
                        ></textarea>
                      </div>
                      <button type="submit" className="btn btn-primary mb-2">
                        Submit Review
                      </button>
                    </form>
                    {reviews.map((review, idx) => (
                      <div key={idx} className="card mb-2">
                        <div className="card-body">
                          <h6 className="card-title">Review:</h6>
                          <p className="card-text">{review}</p>
                          <form onSubmit={this.handleCommentFormSubmit}>
                            <div className="form-row align-items-center">
                              <div className="col">
                                <input
                                  type="email"
                                  className="form-control mb-2"
                                  placeholder="Email"
                                  value={userEmail}
                                  onChange={this.handleEmailInputChange}
                                />
                              </div>
                              <div className="col">
                                <select
                                  className="form-control mb-2"
                                  value={userStatus}
                                  onChange={this.handleStatusInputChange}
                                >
                                  <option value="">Select Status</option>
                                  <option value="Former Tenant">Former Tenant</option>
                                  <option value="Current Tenant">Current Tenant</option>
                                </select>
                              </div>
                              <div className="col">
                                <textarea
                                  className="form-control mb-2"
                                  placeholder="Add your comment..."
                                  value={newComment}
                                  onChange={this.handleCommentInputChange}
                                ></textarea>
                              </div>
                              <div className="col">
                                <button type="submit" className="btn btn-primary mb-2">
                                  Submit Comment
                                </button>
                              </div>
                            </div>
                          </form>
                          {comments.map((comment, i) => (
                            <div key={i} className="card bg-light mt-2">
                              <div className="card-body">
                                <p className="card-text">
                                  <strong>Email:</strong> {userEmail}
                                </p>
                                <p className="card-text">
                                  <strong>Status:</strong> {userStatus}
                                </p>
                                <p className="card-text">{comment}</p>
                              </div>
                            </div>
                          ))}
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  }
}

export default App;
