import React from 'react';

class ReviewAudiences extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      commentInputs: {},
    };
  }

  handleInputChange = (event, index) => {
    const { value } = event.target;
    this.setState((prevState) => ({
      commentInputs: {
        ...prevState.commentInputs,
        [index]: value,
      },
    }));
  };

  handleFormSubmit = (event, index) => {
    event.preventDefault();
    const { commentInputs } = this.state;
    const comment = commentInputs[index] || '';

    if (comment.trim() !== '') {
      this.setState((prevState) => ({
        commentInputs: {
          ...prevState.commentInputs,
          [index]: '',
        },
      }));
    }
  };

  render() {
    const { commentInputs } = this.state;
    const { houses } = this.props;

    return (
      <div className="container mt-5">
        <h2>Review Audiences</h2>
        <ul className="list-group">
          {houses.map((house, index) => (
            <li key={index} className="list-group-item">
              <div className="d-flex align-items-center">
                <img
                  src={house.image}
                  alt="House"
                  className="rounded mr-2"
                  style={{ width: '100px', height: '100px' }}
                />
                <div>
                  <h5>Address: {house.address}</h5>
                  <p>Type: {house.type}</p>
                </div>
              </div>
              <div className="mt-2">
                <form onSubmit={(event) => this.handleFormSubmit(event, index)}>
                  <div className="form-group">
                    <label htmlFor={`comment-${index}`}>Add your comment:</label>
                    <input
                      type="text"
                      className="form-control"
                      id={`comment-${index}`}
                      placeholder="Enter your comment"
                      value={commentInputs[index] || ''}
                      onChange={(event) => this.handleInputChange(event, index)}
                    />
                  </div>
                  <button className="btn btn-primary" type="submit">
                    Submit
                  </button>
                </form>
              </div>
            </li>
          ))}
        </ul>
      </div>
    );
  }
}

export default ReviewAudiences;
