import React from 'react';
import Reviewer from './Reviewer';
import ReviewAudiences from './ReviewAudiences';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      reviews: [],
    };
  }

  handleReviewSubmit = (review) => {
    // Add the review to the reviews array in state
    this.setState((prevState) => ({
      reviews: [...prevState.reviews, review],
    }));
  };

  render() {
    return (
      <div className="App">
        {/* Rest of the code */}
        <Reviewer onReviewSubmit={this.handleReviewSubmit} />
        <ReviewAudiences reviews={this.state.reviews} />
      </div>
    );
  }
}

export default App;
