import React from 'react';

class ReviewAudiences extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      comments: [],
      newComment: '',
    };
  }

  handleInputChange = (event) => {
    this.setState({ newComment: event.target.value });
  };

  handleFormSubmit = (event) => {
    event.preventDefault();
    const { newComment } = this.state;
    if (newComment.trim() !== '') {
      this.setState((prevState) => ({
        comments: [...prevState.comments, newComment],
        newComment: '',
      }));
    }
  };

  render() {
    const { comments, newComment } = this.state;
    const houses = [
      {
        image:
          'https://i2.au.reastatic.net/800x600-format=webp/834c6299504b1fa49683d16ec8aad29a115b2c914e5e4f2922e6ec46d0981075/image.jpg',
        address: '1105/442 Elizabeth Street, Melbourne, Vic 3000',
        type: 'Apartment',
      },
      {
        image:
          'https://i2.au.reastatic.net/800x600-format=webp/41f681b4e16b15fab09fe9feca04ea93b0f275a9168f1e1157980f4ffa83da95/image.jpg',
        address: '163 Albert Street, Port Melbourne, Vic 3207',
        type: 'Sharehouse',
      },
      {
        image:
          'https://i2.au.reastatic.net/600x800-resize,extend,r=33,g=40,b=46/e8e44ba7cbc7eec3f3e7681842b9dd6d3dc5375dbe1d072e172687996831b898/image.jpg',
        address: '108/593 Elizabeth Street, Melbourne, Vic 3000',
        type: 'Student Accommodation',
      },
    ];

    return (
      <div className="container mt-5">
        <h2>Review Audiences</h2>
        <ul className="list-group">
          {houses.map((house, index) => (
            <li key={index} className="list-group-item">
              <div className="d-flex align-items-center">
                <img
                  src={house.image}
                  alt="House"
                  className="rounded mr-2"
                  style={{ width: '100px', height: '100px' }}
                />
                <div>
                  <h5>Address: {house.address}</h5>
                  <p>Type: {house.type}</p>
                </div>
              </div>
              <div className="mt-2">
                <form onSubmit={this.handleFormSubmit}>
                  <div className="form-group">
                    <label htmlFor={`comment-${index}`}>Add your comment:</label>
                    <input
                      type="text"
                      className="form-control"
                      id={`comment-${index}`}
                      placeholder="Enter your comment"
                      value={newComment}
                      onChange={this.handleInputChange}
                    />
                  </div>
                  <button className="btn btn-primary" type="submit">
                    Submit
                  </button>
                </form>
              </div>
              <ul className="list-group mt-2">
                {comments.map((comment, idx) => (
                  <li key={idx} className="list-group-item">
                    <div className="d-flex align-items-center">
                      <img
                        src={`https://dummyimage.com/50x50/c0c0c0/ffffff&text=Profile`}
                        alt="Profile"
                        className="rounded-circle mr-2"
                      />
                      {comment}
                    </div>
                  </li>
                ))}
              </ul>
            </li>
          ))}
        </ul>
      </div>
    );
  }
}

export default ReviewAudiences;
