import React from 'react';
import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      reviews: [],
      newReview: '',
      comments: [],
      newComment: '',
      listings: [
        {
          image: 'https://i2.au.reastatic.net/800x600-format=webp/834c6299504b1fa49683d16ec8aad29a115b2c914e5e4f2922e6ec46d0981075/image.jpg',
          address: '1105/442 Elizabeth Street, Melbourne, VIC 3000',
          type: 'Apartment',
        },
        {
          image: 'https://i2.au.reastatic.net/800x600-format=webp/41f681b4e16b15fab09fe9feca04ea93b0f275a9168f1e1157980f4ffa83da95/image.jpg',
          address: '163 Albert Street, Port Melbourne, VIC 3207',
          type: 'Sharehouse',
        },
        {
          image: 'https://i2.au.reastatic.net/1096x744-resize,extend,r=33,g=40,b=46/166b0483a7db77277c3cadea57848c2c2f8818855c18492941361ee549437b6b/image.jpg',
          address: '108/593 Elizabeth Street, Melbourne, VIC 3000',
          type: 'Student Accommodation',
        },
      ],
      userEmail: '',
      userStatus: '',
    };
  }

  handleReviewInputChange = (event) => {
    this.setState({ newReview: event.target.value });
  };

  handleCommentInputChange = (event) => {
    this.setState({ newComment: event.target.value });
  };

  handleReviewFormSubmit = (event, index) => {
    event.preventDefault();
    const { newReview, reviews } = this.state;
    if (newReview.trim() !== '') {
      const updatedReviews = [...reviews];
      updatedReviews[index] = newReview;
      this.setState({
        reviews: updatedReviews,
        newReview: '',
      });
    }
  };

  handleCommentFormSubmit = (event) => {
    event.preventDefault();
    const { newComment, userEmail, userStatus, comments } = this.state;
    if (newComment.trim() !== '') {
      const newCommentObj = {
        email: userEmail,
        status: userStatus,
        comment: newComment,
      };
      this.setState((prevState) => ({
        comments: [...prevState.comments, newCommentObj],
        newComment: '',
      }));
    }
  };

  handleEmailInputChange = (event) => {
    this.setState({ userEmail: event.target.value });
  };

  handleStatusInputChange = (event) => {
    this.setState({ userStatus: event.target.value });
  };

  render() {
    const { listings, reviews, newReview, comments, newComment, userEmail, userStatus } = this.state;

    return (
      <div>
        <div className="container mt-5" id="review">
          <h2>Reviews</h2>
          <div className="row">
            {listings.map((listing, index) => (
              <div key={index} className="col-md-4 mb-4">
                <div className="card">
                  <img src={listing.image} className="card-img-top" alt="House" />
                  <div className="card-body">
                    <h5 className="card-title">Address: {listing.address}</h5>
                    <p className="card-text">Type: {listing.type}</p>
                    <form onSubmit={(event) => this.handleReviewFormSubmit(event, index)}>
                      <div className="form-group">
                        <label htmlFor="reviewInput">Add Review Summary:</label>
                        <textarea
                          className="form-control"
                          id="reviewInput"
                          rows="3"
                          value={newReview}
                          onChange={this.handleReviewInputChange}
                        ></textarea>
                      </div>
                      <button type="submit" className="btn btn-primary mb-2">
                        Submit Review
                      </button>
                    </form>
                    {reviews[index] && (
                      <div className="card mb-2">
                        <div className="card-body">
                          <form onSubmit={this.handleCommentFormSubmit}>
                            <div className="form-row align-items-center">
                              <div className="col">
                                <input
                                  type="email"
                                  className="form-control mb-2"
                                  placeholder="Email"
                                  value={userEmail}
                                  onChange={this.handleEmailInputChange}
                                />
                              </div>
                              <div className="col">
                                <select
                                  className="form-control mb-2"
                                  value={userStatus}
                                  onChange={this.handleStatusInputChange}
                                >
                                  <option value="">Select Status</option>
                                  <option value="Former Tenant">Former Tenant</option>
                                  <option value="Current Tenant">Current Tenant</option>
                                </select>
                              </div>
                              <div className="col">
                                <textarea
                                  className="form-control mb-2"
                                  placeholder="Add your comment..."
                                  value={newComment}
                                  onChange={this.handleCommentInputChange}
                                ></textarea>
                              </div>
                              <div className="col">
                                <button type="submit" className="btn btn-primary mb-2">
                                  Submit Comment
                                </button>
                              </div>
                            </div>
                          </form>
                          {comments.map((comment, i) => (
                            <div key={i} className="card bg-light mt-2">
                              <div className="card-body">
                                <p className="card-text">
                                  <h3 className="card-text">{reviews[index]}</h3>
                                  <strong>Email:</strong> {comment.email}
                                </p>
                                <p className="card-text">
                                  <strong>Status:</strong> {comment.status}
                                </p>
                                <p className="card-text">
                                  <strong>Comment:</strong> {comment.comment}
                                </p>
                              </div>
                            </div>
                          ))}
                        </div>
                      </div>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  }
}

export default App;
