import React from 'react';

class ReviewAudiences extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      reviews: [
        {
          house: {
            image: 'https://i2.au.reastatic.net/800x600-format=webp/834c6299504b1fa49683d16ec8aad29a115b2c914e5e4f2922e6ec46d0981075/image.jpg',
            address: '1105/442 Elizabeth Street, Melbourne, VIC 3000',
            type: 'Apartment',
          },
          review: '',
        },
        {
          house: {
            image: 'https://i2.au.reastatic.net/800x600-format=webp/41f681b4e16b15fab09fe9feca04ea93b0f275a9168f1e1157980f4ffa83da95/image.jpg',
            address: '163 Albert Street, Port Melbourne, VIC 3207',
            type: 'Sharehouse',
          },
          review: '',
        },
        {
          house: {
            image: 'https://i2.au.reastatic.net/600x800-resize,extend,r=33,g=40,b=46/e8e44ba7cbc7eec3f3e7681842b9dd6d3dc5375dbe1d072e172687996831b898/image.jpg',
            address: '108/593 Elizabeth Street, Melbourne, VIC 3000',
            type: 'Student Accommodation',
          },
          review: '',
        },
      ],
    };
  }

  handleReviewInputChange = (event, index) => {
    const { reviews } = this.state;
    reviews[index].review = event.target.value;
    this.setState({ reviews });
  };

  render() {
    const { reviews } = this.state;

    return (
      <div>
        <h2>Review Audiences</h2>
        {reviews.map((review, index) => (
          <div key={index} className="card mb-3">
            <img src={review.house.image} className="card-img-top" alt="House" />
            <div className="card-body">
              <h5 className="card-title">Address: {review.house.address}</h5>
              <p className="card-text">Type: {review.house.type}</p>
              <div className="form-group">
                <label htmlFor={`reviewInput-${index}`}>Add Review:</label>
                <textarea
                  className="form-control"
                  id={`reviewInput-${index}`}
                  rows="3"
                  value={review.review}
                  onChange={(event) => this.handleReviewInputChange(event, index)}
                ></textarea>
              </div>
            </div>
          </div>
        ))}
      </div>
    );
  }
}

export default ReviewAudiences;
