import React from 'react';
import './App.css';
import { Navbar, Nav } from 'react-bootstrap';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      listings: [
        {
          image: 'https://i2.au.reastatic.net/800x600-format=webp/834c6299504b1fa49683d16ec8aad29a115b2c914e5e4f2922e6ec46d0981075/image.jpg',
          address: '1105/442 Elizabeth Street, Melbourne, VIC 3000',
          type: 'Apartment',
          review: '',
        },
        {
          image: 'https://i2.au.reastatic.net/800x600-format=webp/41f681b4e16b15fab09fe9feca04ea93b0f275a9168f1e1157980f4ffa83da95/image.jpg',
          address: '163 Albert Street, Port Melbourne, VIC 3207',
          type: 'Sharehouse',
          review: '',
        },
        {
          image: 'https://i2.au.reastatic.net/600x800-resize,extend,r=33,g=40,b=46/e8e44ba7cbc7eec3f3e7681842b9dd6d3dc5375dbe1d072e172687996831b898/image.jpg',
          address: '108/593 Elizabeth Street, Melbourne, VIC 3000',
          type: 'Student Accommodation',
          review: '',
        },
      ],
    };
  }

  handleReviewChange = (index, event) => {
    const { listings } = this.state;
    const newReview = event.target.value;
    this.setState((prevState) => {
      const updatedListings = [...prevState.listings];
      updatedListings[index].review = newReview;
      return { listings: updatedListings };
    });
  };

  render() {
    const { listings } = this.state;

    return (
      <div>
        <Navbar bg="light" expand="lg">
          <Navbar.Brand href="/">Ranter</Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="ml-auto">
              <Nav.Link href="#review">Review</Nav.Link>
            </Nav>
          </Navbar.Collapse>
        </Navbar>

        <div className="container mt-5">
          <h2>What is Ranter?</h2>
          <p>
            Are you tired of struggling to find the perfect rental property? Look no further than Ranter! Our platform
            provides tenants with the most current and authentic reviews from previous renters, helping you to find your
            ideal home. Say goodbye to the guesswork and frustration of the rental search process and say hello to the
            ease and convenience of Ranter!
          </p>
        </div>

        <div className="container mt-5" id="review">
          <h2>Reviews</h2>
          {listings.map((listing, index) => (
            <div key={index} className="card mb-3">
              <img src={listing.image} className="card-img-top" alt="House" />
              <div className="card-body">
                <h5 className="card-title">{listing.address}</h5>
                <p className="card-text">Type: {listing.type}</p>
                <form>
                  <div className="form-group">
                    <label htmlFor={`reviewInput-${index}`}>Add Review:</label>
                    <textarea
                      className="form-control"
                      id={`reviewInput-${index}`}
                      rows="3"
                      value={listing.review}
                      onChange={(event) => this.handleReviewChange(index, event)}
                    ></textarea>
                  </div>
                </form>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  }
}

export default App;
